import React from 'react';
import '../Client/CSS/Testimonials.css'
import Gallery from 'react-grid-gallery'
import {TransitionGroup, CSSTransition} from 'react-transition-group';

const doug_and_sue_images =
[{
        src: "https://goo.gl/X43DWJ",
        thumbnail: "https://goo.gl/X43DWJ",
        thumbnailWidth: 150,
        thumbnailHeight: 150,
        caption: "Family Room"
},
{
        src: "https://goo.gl/kTCD6T",
        thumbnail: "https://goo.gl/kTCD6T",
        thumbnailWidth: 150,
        thumbnailHeight: 150,
        caption: "Stairs"
},
{
        src: "https://goo.gl/BcZvzf",
        thumbnail: "https://lh3.googleusercontent.com/riYqxE57pC1jTFtxn3HYXjDsZTxczG6xyoArB5QBJQGn6ZKwBfgJMmut-ERQz4SiQMBxtvTTAu-CgrR0OIupDxBchSUWmlNzSxYRO68_cSfUbXN0XKkB0B98t5y5SJjP2Z-xPskHvzRDeSrNdxPildLAl-YDcT7wVSOs-2bq-dSi3bN8sGOefNlF68TK_PO-SW8MrTgNOmQyihm5euSHOMrE9V4IDS2Qt1UI_y5OG1D-aESTfQ2iEe4kFL0IMnEjkXRnTWb75Oii4MHn49SIKjmiyEPMM3Er5HoNhTZIyUZv-CYXR39QMN303TA51Hkv8KPr8cGvAaMMhGvaiv9DtgXbGkojezBv2JyKKwDkb3b38d-gHJA4PvgQWwqOHNlil1wZH8T_RSitgz4YkvOwDubuAmQslNbG6M1jmX84m-mbcsoMOI7JHPKQn-T3WW7kFeZJKw_8PR4w9Xv-S-N0LsS5IQGaCmpEuGJ5cKWMlaXY8HtJjweBEep4CSivi-8bPgxhOfBTx3Gkk08_aYMTxwjkaIw9FhyaRg9CofcPN84ILZexNBGvx8DLeb2nuP73q-LoK87KDW4I0jJ6DcQhByrMYFpEDz7QR2jrrg613DICrQ0nWY9jdc1sggYx3vAjPlZ1vka8XnnrkkGvBhmp_VA2=s598-no",
        thumbnailWidth: 150,
        thumbnailHeight: 150,
        caption: "Dining Room"
}]

const default_images = 
[
    {
        src: "https://c2.staticflickr.com/9/8817/28973449265_07e3aa5d2e_b.jpg",
        thumbnail: "https://c2.staticflickr.com/9/8817/28973449265_07e3aa5d2e_n.jpg",
        thumbnailWidth: 320,
        thumbnailHeight: 174,
        tags: [{value: "Nature", title: "Nature"}, {value: "Flora", title: "Flora"}],
        caption: "After Rain (Jeshu John - designerspics.com)"
    },
    {
        src: "https://c2.staticflickr.com/9/8356/28897120681_3b2c0f43e0_b.jpg",
        thumbnail: "https://c2.staticflickr.com/9/8356/28897120681_3b2c0f43e0_n.jpg",
        thumbnailWidth: 320,
        thumbnailHeight: 212,
        caption: "Boats (Jeshu John - designerspics.com)"
    }];

class Testimonials extends React.Component {
    render() {
        return (
            <div> 
                <h2>See what people are saying...</h2>
                <section className="testimonial-body" id="doug_and_sue">
                    <p>"Dependable, trustworthy, and expert workmanship all 
                        describe Kevin and his business.  
                        He completely transformed our condo, painting it from top to 
                        bottom, among other projects.
                        Not only does he do excellent work, but he's a 
                        pleasure to have in your home. For any future projects, 
                        there's no one we'd rather have than Kevin." 
                        <span class="testimonial-signature"> - Doug and Sue &sdot; Brookfield, WI</span>
                    </p>
                    <Gallery images={doug_and_sue_images} 
                                backdropClosesModal={true}
                                enableKeyboardInput={true}
                                enableImageSelection={false}/>
                </section>
                <section className="testimonial-body" style={{animationDelay:"0.75s"}} id="section2">
                    <p>"This is another testimonial. This will probably contain
                        information such as how good his work was and stuff like that.
                        Blah blah blah blah blahhhhhhhhhhhhhhhhh."
                        <span class="testimonial-signature">- Some random dude &sdot; Somwhere, Anywhere</span>
                    </p>
                    <Gallery images={default_images} // will change
                             backdropClosesModal={true}
                             enableKeyboardInput={true}
                             enableImageSelection={false}/> 
                </section>
                <section className="testimonial-body" style={{animationDelay:"1.5s"}} id="section3">
                    <p>"This is another testimonial. This will probably contain
                        information such as how good his work was and stuff like that.
                        Blah blah blah blah blahhhhhhhhhhhhhhhhh."
                        <span class="testimonial-signature">- Some random dude &sdot; Somwhere, Anywhere</span>
                    </p>
                    <Gallery images={default_images} // will change
                             backdropClosesModal={true}
                             enableKeyboardInput={true}
                             enableImageSelection={false}/> 
                </section>
                <section className="testimonial-body" style={{animationDelay:"2.25s"}} id="section4">
                    <p>"This is another testimonial. This will probably contain
                        information such as how good his work was and stuff like that.
                        Blah blah blah blah blahhhhhhhhhhhhhhhhh."
                        <span class="testimonial-signature">- Some random dude &sdot; Somwhere, Anywhere</span>
                    </p>
                    <Gallery images={default_images} // will change
                             backdropClosesModal={true}
                             enableKeyboardInput={true}
                             enableImageSelection={false}/> 
                </section>
            </div>
        );
    }
}

export default Testimonials;
